gitaly_active_node_available_space.query:
1 - (
  sum by (%(aggregationLabels)s) (
    (
      node_filesystem_avail_bytes{%(selector)s, %(gitalyDiskSelector)s} -
      (node_filesystem_size_bytes{%(selector)s, %(gitalyDiskSelector)s} * 0.2)
    )
    and
    (instance:node_filesystem_avail:ratio{%(selector)s, %(gitalyDiskSelector)s} > 0.2)
  )
  /
  sum by (%(aggregationLabels)s)(
    node_filesystem_size_bytes{%(selector)s, %(gitalyDiskSelector)s}
  )
)

gitaly_total_disk_space.query:
1 - (
  sum by (%(aggregationLabels)s) (
    node_filesystem_avail_bytes{%(selector)s, mountpoint="/var/opt/gitlab"}
  )
  /
  sum by (%(aggregationLabels)s) (
    node_filesystem_size_bytes{%(selector)s, mountpoint="/var/opt/gitlab"}
  )
)

go_memory.query:
sum by (%(aggregationLabels)s) (
  go_memstats_alloc_bytes{%(selector)s}
)
/
sum by (%(aggregationLabels)s) (
  node_memory_MemTotal_bytes{%(selector)s}
)

kube_container_cpu.query:
sum by (%(aggregationLabels)s) (
  rate(container_cpu_usage_seconds_total:labeled{container!="", container!="POD", %(selector)s}[%(rangeInterval)s])
)
/
sum by(%(aggregationLabels)s) (
  container_spec_cpu_quota:labeled{container!="", container!="POD", %(selector)s}
  /
  container_spec_cpu_period:labeled{container!="", container!="POD", %(selector)s}
)

kube_node_ips.query:
sum(:kube_pod_info_node_count:{%(selector)s}) by (%(aggregationLabels)s)
/
sum(
  gitlab:gcp_subnet_max_ips{%(selector)s} * on (subnet) group_right gitlab:cluster:subnet:mapping{%(selector)s}
) by (%(aggregationLabels)s)

kube_pool_max_nodes.query:
count by (cluster, env, environment, label_pool, tier, type, stage, shard) (
  kube_node_labels:labeled{%(selector)s}
)
/ on(cluster, env, environment, label_pool) group_left() (
  label_replace(
    terraform_report_google_cluster_node_pool_max_node_count,
    "label_pool", "$0", "pool_name", ".*"
  )
  * on(cluster, env, environment) group_left()
  count by (cluster, env, environment) (
    group by (cluster, env, environment, label_topology_kubernetes_io_zone) (
      kube_node_labels:labeled{%(selector)s}
    )
  )
)

nat_gateway_port_allocation.query:
sum without(nat_ip) (
  stackdriver_nat_gateway_router_googleapis_com_nat_allocated_ports{
    job="stackdriver",
    project_id="gitlab-production",
    %(selector)s
  }
)
/
( %(max_ports_per_nat_ip)d * %(gprd_nat_ip_count)d )
or
sum without(nat_ip) (
  stackdriver_nat_gateway_router_googleapis_com_nat_allocated_ports{
    job="stackdriver",
    project_id="gitlab-staging-1",
    %(selector)s
  }
)
/
( %(max_ports_per_nat_ip)d * %(gstg_nat_ip_count)d )

nat_host_port_allocation.query:
sum without(ip_protocol) (
  max_over_time(
    stackdriver_gce_instance_compute_googleapis_com_nat_port_usage{
      job="stackdriver",
      project_id=~"gitlab-production|gitlab-staging-1",
      %(selector)s
    }[%(rangeInterval)s]
  )
)
/
sum without (nat_ip) (
  max_over_time(
    stackdriver_gce_instance_compute_googleapis_com_nat_allocated_ports{
      job="stackdriver",
      project_id=~"gitlab-production|gitlab-staging-1",
      %(selector)s
    }[%(rangeInterval)s]
  )
)

nv_gpu_memory.query:
sum by (%(aggregationLabels)s) (
  nv_gpu_memory_used_bytes{%(selector)s}
)
/
sum by (%(aggregationLabels)s) (
  nv_gpu_memory_total_bytes{%(selector)s}
)

pg_active_db_connections_primary.query:
sum without (state) (
  pg_stat_activity_count{datname=~"gitlabhq_production|gitlabhq_registry", state!="idle", %(selector)s} unless on(instance) (pg_replication_is_replica == 1)
)
/ on (%(aggregationLabels)s)
pg_settings_max_connections{%(selector)s}

pg_active_db_connections_replica.query:
sum without (state) (
  pg_stat_activity_count{datname=~"gitlabhq_production|gitlabhq_registry", state!="idle", %(selector)s} and on(instance) (pg_replication_is_replica == 1)
)
/ on (%(aggregationLabels)s)
pg_settings_max_connections{%(selector)s}

pg_txid_vacuum_to_wraparound.query:
(
  sum by (%(aggregationLabels)s) (
    increase(fluentd_pg_auto_vacuum_elapsed_seconds_total{%(selector)s}[1d])
  )
  / on(environment, type) group_left() (
    pg_settings_autovacuum_max_workers and
    on (instance) pg_replication_is_replica == 0
  )
)
/
(
  %(txWraparoundExpression)s
  /
  (
    avg by (%(aggregationLabels)s) (
      deriv(pg_txid_current{%(selector)s}[1d]) and
      on (instance) pg_replication_is_replica == 0
    )
  )
)

pg_vacuum_activity_v2.query:
sum by (%(aggregationLabels)s) (
  rate(fluentd_pg_auto_vacuum_elapsed_seconds_total{%(selector)s}[1d])
  and on (fqdn) (pg_replication_is_replica{%(selector)s} == 0)
)
/
avg by (%(aggregationLabels)s) (
  pg_settings_autovacuum_max_workers{%(selector)s}
  and on (instance, job) (pg_replication_is_replica{%(selector)s} == 0)
)

pg_walsender_cpu.query:
sum by (%(aggregationLabels)s) (
  sum by(%(aggregationLabels)s, fqdn) (
    rate(namedprocess_namegroup_cpu_seconds_total{%(selector)s, groupname=~"pg.worker.walsender|pg.worker.walwriter|wal-g"}[%(rangeInterval)s])
    and on (fqdn) (pg_replication_is_replica{%(selector)s} == 0)
  )
  /
  count by (%(aggregationLabels)s, fqdn) (
    node_cpu_seconds_total{%(selector)s, mode="idle"} and on(fqdn) (pg_replication_is_replica{%(selector)s} == 0)
  )
)

